contracts/Disperse.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IERC20 {
	function transfer(address to, uint256 value) external returns (bool);
	function transferFrom(
		address from,
		address to,
		uint256 value
	) external returns (bool);
}

contract Disperse {
	function disperseEther(
		address[] calldata recipients,
		uint256[] calldata values
	) external payable {
		for (uint256 i = 0; i < recipients.length; i++) {
			(bool success, ) = recipients[i].call{ value: values[i] }("");
			require(success, "Transfer failed.");
		}
		uint256 balance = address(this).balance;
		if (balance > 0) {
			(bool success, ) = msg.sender.call{ value: balance }("");
			require(success, "Transfer failed.");
		}
	}

	function disperseToken(
		IERC20 token,
		address[] calldata recipients,
		uint256[] calldata values
	) external {
		for (uint256 i = 0; i < recipients.length; i++) {
			require(
				token.transferFrom(msg.sender, recipients[i], values[i]),
				"TransferFrom failed."
			);
		}
	}
}

contracts/ERC20Factory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "./ERC20Ownable.sol";

contract ERC20Factory is Ownable {
	string public name;
	mapping(address => address[]) public tokensByOwner;
	address[] public allTokens;

	event TokenCreated(address indexed owner, address token);
	event TokenOwnershipUpdated(address indexed oldOwner, address indexed newOwner, address token);
	event TokenLocked(address indexed token);
	event TokenUnlocked(address indexed token);

	constructor(string memory _name) Ownable() {
		transferOwnership(msg.sender);
		name = _name;
	}

	function createToken(
		string memory name_,
		string memory symbol_,
		address tokenOwner_,
		address associatedNFT_,
		uint256 associatedNFTId_,
		address[] memory membersToFund,
		uint256[] memory amountsToFund
	) external virtual returns (address) {
		ERC20Ownable token = new ERC20Ownable(
			name_,
			symbol_,
			tokenOwner_,
			address(this),
			associatedNFT_,
			associatedNFTId_,
			membersToFund,
			amountsToFund
		);

		tokensByOwner[tokenOwner_].push(address(token));
		allTokens.push(address(token));

		emit TokenCreated(tokenOwner_, address(token));
		return address(token);
	}

	function getTokensByOwner(address owner) external view returns (address[] memory) {
		return tokensByOwner[owner];
	}

	function getAllTokens() external view returns (address[] memory) {
		return allTokens;
	}

	function updateTokenOwnership(address token, address newOwner) external onlyOwner {
		require(ERC20Ownable(token).factory() == address(this), "Invalid token");

		address oldOwner = ERC20Ownable(token).owner();
		ERC20Ownable(token).transferOwnership(newOwner);

		_updateOwnership(token, oldOwner, newOwner);
	}

	function lockToken(address token) external onlyOwner {
		require(ERC20Ownable(token).factory() == address(this), "Invalid token");
		ERC20Ownable(token).lock();
		emit TokenLocked(token);
	}

	function unlockToken(address token) external onlyOwner {
		require(ERC20Ownable(token).factory() == address(this), "Invalid token");
		ERC20Ownable(token).unlock();
		emit TokenUnlocked(token);
	}

	function notifyOwnershipChange(address oldOwner, address newOwner) external {
		require(ERC20Ownable(msg.sender).factory() == address(this), "Invalid caller");

		// Check if the token address exists in the allTokens array
		bool tokenExists = false;
		for (uint256 i = 0; i < allTokens.length; i++) {
			if (allTokens[i] == msg.sender) {
				tokenExists = true;
				break;
			}
		}

		// If the token address does not exist in the array, add it
		if (!tokenExists) {
			allTokens.push(msg.sender);
		}

		_updateOwnership(msg.sender, oldOwner, newOwner);
	}

	function _updateOwnership(address token, address oldOwner, address newOwner) internal {
		// Remove token from old owner
		address[] storage oldOwnerTokens = tokensByOwner[oldOwner];
		for (uint256 i = 0; i < oldOwnerTokens.length; i++) {
			if (oldOwnerTokens[i] == token) {
				oldOwnerTokens[i] = oldOwnerTokens[oldOwnerTokens.length - 1];
				oldOwnerTokens.pop();
				break;
			}
		}

		// Add token to new owner
		tokensByOwner[newOwner].push(token);

		emit TokenOwnershipUpdated(oldOwner, newOwner, token);
	}
}

contracts/ERC20FactoryKyc.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./ERC20OwnableKyc.sol";
import "./ERC20Factory.sol";

contract ERC20FactoryKyc is ERC20Factory {
	constructor(string memory _name) ERC20Factory(_name) {}

	function createToken(
		string memory name_,
		string memory symbol_,
		address tokenOwner_,
		address associatedNFT_,
		uint256 associatedNFTId_,
		address[] memory membersToFund,
		uint256[] memory amountsToFund
	) external override returns (address) {
		ERC20OwnableKyc token = new ERC20OwnableKyc(
			name_,
			symbol_,
			tokenOwner_,
			address(this),
			associatedNFT_,
			associatedNFTId_,
			membersToFund,
			amountsToFund
		);

		tokensByOwner[tokenOwner_].push(address(token));
		allTokens.push(address(token));

		emit TokenCreated(tokenOwner_, address(token));
		return address(token);
	}
}

contracts/ERC20Ownable.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./ERC20Factory.sol";

contract ERC20Ownable is ERC20, Ownable, Pausable {
	address public factory;
	address public linkedNFT;
	uint256 public linkedNFTId;
	string public status;
	bool public locked = false;

	// Add state variables for name and symbol
	string private _customName;
	string private _customSymbol;

	event Locked();
	event Unlocked();
	event NFTDataUpdated(address indexed newAddress, uint256 id);
	event TokensMinted(address indexed to, uint256 amount);
	event TokensBurned(address indexed from, uint256 amount);
	event NameChanged(string newName);
	event SymbolChanged(string newSymbol);
	event StatusChanged(string newStatus);

	modifier notLocked() {
		require(!locked, "Contract is locked");
		_;
	}

	constructor(
		string memory name_,
		string memory symbol_,
		address owner_,
		address factory_,
		address linkedNFT_,
		uint256 linkedNFTId_,
		address[] memory membersToFund,
		uint256[] memory amountsToFund
	) ERC20(name_, symbol_) Ownable() {
		transferOwnership(owner_);
		require(membersToFund.length == amountsToFund.length, "Mismatched input lengths");
		status = "active";
		factory = factory_;
		linkedNFT = linkedNFT_;
		linkedNFTId = linkedNFTId_;
		_customName = name_;
		_customSymbol = symbol_;

		for (uint256 i = 0; i < membersToFund.length; i++) {
			_mint(membersToFund[i], amountsToFund[i]);
		}
	}

	// Override name and symbol functions to return the custom values
	function name() public view virtual override returns (string memory) {
		return _customName;
	}

	function symbol() public view virtual override returns (string memory) {
		return _customSymbol;
	}

	function mint(address to, uint256 amount) external onlyOwner notLocked whenNotPaused {
		_mint(to, amount);
		emit TokensMinted(to, amount);
	}

	function burn(address from, uint256 amount) external onlyOwner notLocked whenNotPaused {
		_burn(from, amount);
		emit TokensBurned(from, amount);
	}

	function updateNFTData(address linkedNFT_, uint256 linkedNFTId_) external onlyOwner notLocked whenNotPaused {
		linkedNFT = linkedNFT_;
		linkedNFTId = linkedNFTId_;
		emit NFTDataUpdated(linkedNFT_, linkedNFTId_);
	}

	function changeStatus(string memory newStatus) external onlyOwner notLocked whenNotPaused {
		status = newStatus;
		emit StatusChanged(newStatus);
	}

	function changeName(string memory newName) external onlyOwner notLocked whenNotPaused {
		_customName = newName;
		emit NameChanged(newName);
	}

	function changeSymbol(string memory newSymbol) external onlyOwner notLocked whenNotPaused {
		_customSymbol = newSymbol;
		emit SymbolChanged(newSymbol);
	}

	function lock() external {
		require(msg.sender == factory || msg.sender == owner(), "Only factory or owner can lock");
		locked = true;
		emit Locked();
	}

	function unlock() external {
		require(msg.sender == factory, "Only factory can unlock");
		locked = false;
		emit Unlocked();
	}

	function _transferOwnership(address newOwner) internal override {
		address oldOwner = owner();
		super._transferOwnership(newOwner);
		// Check if the factory address is defined and not an empty address
		if (factory != address(0)) {
			ERC20Factory(factory).notifyOwnershipChange(oldOwner, newOwner);
		}
		emit OwnershipTransferred(oldOwner, newOwner);
	}
}

contracts/ERC20OwnableKyc.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./ERC20Ownable.sol";

interface IKintoKYC {
	function isKYC(address user) external view returns (bool);
	function isSanctionsSafe(address user) external view returns (bool);
}

contract ERC20OwnableKyc is ERC20Ownable {
	// State variables for whitelist and KYC/sanctions checks
	mapping(address => bool) private whitelist;
	bool public whitelistEnabled = false;
	bool public kycCheckEnabled = false;
	// this along with other constant variables for kinto => https://docs.kinto.xyz/kinto-the-safe-l2/building-on-kinto/network-information
	address public kycContract = 0x33F28C3a636B38683a38987100723f2e2d3d038e;

	// Events for whitelist and KYC/sanctions checks
	event WhitelistUpdated(address indexed account, bool isWhitelisted);
	event WhitelistStatusChanged(bool isEnabled);
	event KYCCheckStatusChanged(bool isEnabled);
	event SanctionsCheckStatusChanged(bool isEnabled);
	event KYCContractSet(address kycContract_);

	constructor(
		string memory name_,
		string memory symbol_,
		address owner_,
		address factory_,
		address linkedNFT_,
		uint256 linkedNFTId_,
		address[] memory membersToFund,
		uint256[] memory amountsToFund
	) ERC20Ownable(name_, symbol_, owner_, factory_, linkedNFT_, linkedNFTId_, membersToFund, amountsToFund) {}

	// Function to update the whitelist
	function updateWhitelist(address account, bool isWhitelisted) external onlyOwner {
		whitelist[account] = isWhitelisted;
		emit WhitelistUpdated(account, isWhitelisted);
	}

	// Function to enable/disable the whitelist
	function toggleWhitelist(bool enabled) external onlyOwner {
		whitelistEnabled = enabled;
		emit WhitelistStatusChanged(enabled);
	}

	// Function to enable/disable KYC checks
	function toggleKYCCheck(bool enabled) external onlyOwner {
		kycCheckEnabled = enabled;
		emit KYCCheckStatusChanged(enabled);
	}

	function setKYCContract(address kycContract_) external onlyOwner {
		kycContract = kycContract_;
		emit KYCContractSet(kycContract_);
	}

	function _beforeTokenTransfer(address from, address to, uint256 amount) internal override {
		super._beforeTokenTransfer(from, to, amount);

		// Check whitelist
		if (whitelistEnabled) {
			require(whitelist[to], "Recipient is not whitelisted");
		}

		// Check KYC
		if (kycCheckEnabled) {
			require(
				IKintoKYC(kycContract).isKYC(to) && IKintoKYC(kycContract).isSanctionsSafe(to),
				"Recipient has not passed KYC"
			);
		}
	}
}

contracts/NFTFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "./ERC20Factory.sol";

contract NFTFactory is ERC721URIStorage, Ownable, Pausable {
	using Counters for Counters.Counter;
	Counters.Counter private _tokenIdCounter;
	address public linkedTokenFactoryAddress = 0x0000000000000000000000000000000000000000;
	bool public onlyOwnerCanMint = false;

	struct NFTData {
		string status;
		address linkedToken;
		string[] linkedTokenInterfaces;
		bool locked;
		bool paused;
	}

	mapping(uint256 => NFTData) public nftData;
	mapping(address => uint256[]) internal tokensByAddress;

	function getTokensByAddress(address owner) public view returns (uint256[] memory) {
		return tokensByAddress[owner];
	}

	event TokenMinted(uint256 indexed nftId, address indexed tokenAddress);
	event MetadataLocked(uint256 tokenId);
	event MetadataUnlocked(uint256 tokenId);
	event TokenPaused(uint256 tokenId);
	event TokenUnpaused(uint256 tokenId);
	event TokenBurned(uint256 tokenId);

	constructor(string memory _name, string memory _symbol) ERC721(_name, _symbol) {}

	function setOnlyOwnerCanMint(bool _onlyOwnerCanMint) external onlyOwner {
		onlyOwnerCanMint = _onlyOwnerCanMint;
	}
	function setLinkedTokenFactory(address newFactoryAddress) external onlyOwner {
		linkedTokenFactoryAddress = newFactoryAddress;
	}

	function mint(
		address to,
		string memory tokenURI,
		address existingTokenToLink,
		string[] memory existingLinkedTokenInterfaces,
		string memory name_,
		string memory symbol_,
		address[] memory membersToFund,
		uint256[] memory amountsToFund
	) public virtual whenNotPaused returns (uint256) {
		require(!onlyOwnerCanMint || msg.sender == owner(), "Minting is restricted to the owner");

		// increment id & mint
		uint256 tokenId = _tokenIdCounter.current();
		_tokenIdCounter.increment();
		_safeMint(to, tokenId);
		_setTokenURI(tokenId, tokenURI);

		address linkedTokenAddress;
		string[] memory linkedTokenInterfaces;

		// Check if a existingTokenToLink is provided, or if required parameters are empty
		if (
			existingTokenToLink == address(0) &&
			bytes(name_).length > 0 &&
			bytes(symbol_).length > 0 &&
			membersToFund.length > 0 &&
			amountsToFund.length > 0
		) {
			// TODO: find out why this is throwing error
			// Create the associated ERC20 token by calling TokenFactory
			// linkedTokenInterfaces[0] = "ERC20";
			linkedTokenAddress = ERC20Factory(linkedTokenFactoryAddress).createToken(
				name_,
				symbol_,
				to,
				address(this),
				tokenId,
				membersToFund,
				amountsToFund
			);
		} else {
			// If no token is created, use the provided existingTokenToLink or set to zero address
			linkedTokenAddress = existingTokenToLink;
			linkedTokenInterfaces = existingLinkedTokenInterfaces;
		}

		// set applicable data to the nft
		nftData[tokenId] = NFTData({
			status: "active",
			linkedToken: linkedTokenAddress,
			linkedTokenInterfaces: linkedTokenInterfaces,
			locked: false,
			paused: false
		});



		// tokensByAddress[to].push(tokenId); // Add token to the new owner's list

		emit TokenMinted(tokenId, linkedTokenAddress);
		return tokenId;
	}

	// Callable by both owner and individual NFT holder
	function updateNFT(uint256 tokenId, string memory status, string memory tokenURI) public {
		require(_exists(tokenId), "NFT does not exist");
		require(msg.sender == owner() || msg.sender == ownerOf(tokenId), "Caller is not the owner or NFT owner");
		require(!nftData[tokenId].locked, "Metadata is locked");

		if (bytes(status).length > 0) nftData[tokenId].status = status;
		if (bytes(tokenURI).length > 0) _setTokenURI(tokenId, tokenURI);
	}

	// Callable by both owner and individual NFT holder
	// If called by NFT holder, then it is one-way and needs admin to unlock
	function lock(uint256 tokenId) public {
		require(_exists(tokenId), "NFT does not exist");
		require(msg.sender == owner() || msg.sender == ownerOf(tokenId), "Caller is not the owner or NFT owner");

		nftData[tokenId].locked = true;
		emit MetadataLocked(tokenId);
	}

	function unlock(uint256 tokenId) public onlyOwner {
		require(_exists(tokenId), "NFT does not exist");
		nftData[tokenId].locked = false;
		emit MetadataUnlocked(tokenId);
	}

	function pauseAll() public onlyOwner {
		_pause();
	}

	function unpauseAll() public onlyOwner {
		_unpause();
	}

	function pauseToken(uint256 tokenId) public {
		require(_exists(tokenId), "NFT does not exist");
		require(msg.sender == owner() || msg.sender == ownerOf(tokenId), "Caller is not the owner or NFT owner");
		nftData[tokenId].paused = true;
		emit TokenPaused(tokenId);
	}

	function unpauseToken(uint256 tokenId) public onlyOwner {
		require(_exists(tokenId), "NFT does not exist");
		require(msg.sender == owner() || msg.sender == ownerOf(tokenId), "Caller is not the owner or NFT owner");
		nftData[tokenId].paused = false;
		emit TokenUnpaused(tokenId);
	}

	function burn(uint256 tokenId) public {
		require(_exists(tokenId), "NFT does not exist");
		require(msg.sender == owner() || msg.sender == ownerOf(tokenId), "Caller is not the owner or NFT owner");

		// Burn the NFT
		_burn(tokenId);
		emit TokenBurned(tokenId);
	}

	function _beforeTokenTransfer(
		address from,
		address to,
		uint256 tokenId,
		uint256 batchSize
	) internal virtual override whenNotPaused {
		require(!nftData[tokenId].paused, "Token is paused");

		if (from != address(0)) {
			// Remove token from the previous owner's list
			uint256 index;
			uint256[] storage tokens = tokensByAddress[from];
			for (uint256 i = 0; i < tokens.length; i++) {
				if (tokens[i] == tokenId) {
					index = i;
					break;
				}
			}
			tokens[index] = tokens[tokens.length - 1];
			tokens.pop();
		}

		if (to != address(0)) {
			// Add token to the new owner's list
			tokensByAddress[to].push(tokenId);
		}

		super._beforeTokenTransfer(from, to, tokenId, batchSize);
	}
}

contracts/NFTFactoryKyc.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/utils/Counters.sol";
import "./NFTFactory.sol";
import "./ERC20FactoryKyc.sol";

contract NFTFactoryKyc is NFTFactory {
	using Counters for Counters.Counter;
	Counters.Counter private _tokenIdCounter;
	address public kycContract = 0x33F28C3a636B38683a38987100723f2e2d3d038e;

	struct NFTKYCData {
		bool kycCheckEnabled;
		bool whitelistEnabled;
		mapping(address => bool) whitelist;
	}

	mapping(uint256 => NFTKYCData) public nftKycData;

	event KYCCheckEnabled(uint256 tokenId, bool enabled);
	event SanctionsCheckEnabled(uint256 tokenId, bool enabled);
	event WhitelistEnabled(uint256 tokenId, bool enabled);
	event KYCContractUpdated(address kycContract);
	event AddressWhitelisted(uint256 tokenId, address user, bool status);

	constructor(string memory _name, string memory _symbol) NFTFactory(_name, _symbol) {}

	// Overriding the mint function to handle additional parameters and whitelist initialization
	function mint(
		address to,
		string memory tokenURI,
		address existingLinkedToken,
		string[] memory existingLinkedTokenInterfaces,
		string memory name_, // erc20 mint logic
		string memory symbol_,
		address[] memory membersToFund,
		uint256[] memory amountsToFund
	) public override returns (uint256) {
		require(!onlyOwnerCanMint || msg.sender == owner(), "Minting is restricted to the owner");

		// increment id & mint
		uint256 tokenId = _tokenIdCounter.current();
		_tokenIdCounter.increment();
		_safeMint(to, tokenId);
		_setTokenURI(tokenId, tokenURI);

		address linkedTokenAddress;
		string[] memory linkedTokenInterfaces;

		// Check if a existingLikedToken is provided, or if required parameters are empty
		if (
			existingLinkedToken == address(0) &&
			bytes(name_).length > 0 &&
			bytes(symbol_).length > 0 &&
			membersToFund.length > 0 &&
			amountsToFund.length > 0
		) {
			// TODO: find out why this is throwing error
			// Create the associated ERC20 token by calling TokenFactory
			// linkedTokenInterfaces[0] = "ERC20";
			linkedTokenAddress = ERC20FactoryKyc(linkedTokenFactoryAddress).createToken(
				name_,
				symbol_,
				to,
				address(this),
				tokenId,
				membersToFund,
				amountsToFund
			);
		} else {
			// If no token is created, use the provided existingLikedToken or set to zero address
			linkedTokenAddress = existingLinkedToken;
			linkedTokenInterfaces = existingLinkedTokenInterfaces;
		}

		// Initialize the struct without the mapping because of nested mapping error
		nftData[tokenId].status = "active";
		nftData[tokenId].linkedToken = linkedTokenAddress;
		nftData[tokenId].linkedTokenInterfaces = linkedTokenInterfaces;
		nftData[tokenId].locked = false;
		nftData[tokenId].paused = false;
		nftKycData[tokenId].kycCheckEnabled = false;
		nftKycData[tokenId].whitelistEnabled = false;

		emit TokenMinted(tokenId, to);
		return tokenId;
	}

	// Callable by both owner and individual NFT holder
	function updateNFT(
		uint256 tokenId,
		string memory status,
		string memory tokenURI,
		bool kycCheckEnabled,
		bool whitelistEnabled,
		address[] memory whitelistAddresses
	) public {
		require(_exists(tokenId), "NFT does not exist");
		require(msg.sender == owner() || msg.sender == ownerOf(tokenId), "Caller is not the owner or NFT owner");
		require(!nftData[tokenId].locked, "Metadata is locked");

		if (bytes(status).length > 0) nftData[tokenId].status = status;
		if (bytes(tokenURI).length > 0) _setTokenURI(tokenId, tokenURI);

		// Set kycCheckEnabled if provided
		if (kycCheckEnabled) {
			nftKycData[tokenId].kycCheckEnabled = kycCheckEnabled;
		}

		// Set whitelistEnabled if provided
		if (whitelistEnabled) {
			nftKycData[tokenId].whitelistEnabled = whitelistEnabled;
		}

		// Set whitelist addresses if provided
		if (whitelistAddresses.length > 0) {
			for (uint256 i = 0; i < whitelistAddresses.length; i++) {
				nftKycData[tokenId].whitelist[whitelistAddresses[i]] = true;
			}
		}
	}

	function setKYCContract(address kycContract_) public onlyOwner {
		kycContract = kycContract_;
		emit KYCContractUpdated(kycContract_);
	}

	function _beforeTokenTransfer(
		address from,
		address to,
		uint256 tokenId,
		uint256 batchSize
	) internal virtual override whenNotPaused {
		require(!nftData[tokenId].paused, "Token is paused");

		// Check if KYC is enabled and validate with KYC contract
		if (nftKycData[tokenId].kycCheckEnabled) {
			require(
				IKintoKYC(kycContract).isKYC(to) && IKintoKYC(kycContract).isSanctionsSafe(to),
				"Recipient has not passed KYC or is not SanctionsSafe"
			);
		}

		// Check if whitelist is enabled and the recipient is in the whitelist
		if (nftKycData[tokenId].whitelistEnabled) {
			require(nftKycData[tokenId].whitelist[to], "Recipient is not in the whitelist");
		}

		if (from != address(0)) {
			// Remove token from the previous owner's list
			uint256 index;
			uint256[] storage tokens = tokensByAddress[from];
			for (uint256 i = 0; i < tokens.length; i++) {
				if (tokens[i] == tokenId) {
					index = i;
					break;
				}
			}
			tokens[index] = tokens[tokens.length - 1];
			tokens.pop();
		}

		if (to != address(0)) {
			// Add token to the new owner's list
			tokensByAddress[to].push(tokenId);
		}

		super._beforeTokenTransfer(from, to, tokenId, batchSize);
	}
}

contracts/TokenSale.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract TokenSale is Ownable {
	uint256 public feePercent = 3; // Fee percentage, default is 3%
	mapping(address => uint256) public tokenFees; // Accumulated fees per token
	uint256 public ethFees; // Accumulated ETH fees
	address[] public trackedTokens; // List of tokens with accumulated fees
	address public paymentOracle; // Address of the payment oracle

	constructor(address initialOwner, address _paymentOracle) Ownable() {
		transferOwnership(initialOwner);
		paymentOracle = _paymentOracle;
	}

	struct TokenInfo {
		address depositTokenOwner;
		uint256 priceInEth;
		mapping(address => uint256) acceptedTokenPrices;
		address[] acceptedTokens;
		uint256 totalSalesInEth;
		uint256 totalSalesInFiat;
		mapping(address => uint256) totalSalesInTokens;
		uint256 ethBalance; // Balance of ETH sales
		mapping(address => uint256) tokenBalance; // Balance of token sales per purchase token
		uint256 depositTokenBalance;
		bool canBePurchasedInFiat; // Can be purchased with fiat
		uint256 priceInFiat; // Fiat price
		bool isNFT; // Whether this is an NFT listing
		uint256 tokenId; // NFT token ID if this is an NFT listing
	}

	mapping(address => TokenInfo) public depositTokenInfo;
	mapping(address => bool) public isDepositToken;

	// Events
	event TokenDeposited(
		address indexed depositToken,
		address indexed owner,
		uint256 amount,
		uint256 priceInEth
	);
	event PriceUpdated(
		address indexed depositToken,
		address indexed purchaseToken,
		uint256 newPrice
	);
	event TokenPurchased(
		address indexed buyer,
		address indexed depositToken,
		address indexed purchaseToken,
		uint256 amountPaid,
		uint256 amountReceived
	);
	event FeeUpdated(uint256 newFeePercent);
	event TokensWithdrawn(
		address indexed depositToken,
		address indexed owner,
		uint256 amount
	);
	event DepositRemoved(
		address indexed depositToken,
		address indexed owner,
		uint256 remainingAmount
	);
	event FeesCollected(
		uint256 ethAmount,
		address[] tokenAddresses,
		uint256[] tokenAmounts
	);
	event FiatPurchase(
		address indexed buyer,
		address indexed depositToken,
		uint256 amountReceived
	);

	function getAllTokens() external view returns (address[] memory) {
		return trackedTokens;
	}

	// Deposit token function
	function createDeposit(
		address depositToken,
		uint256 amount,
		uint256 priceInEth,
		address[] calldata acceptedTokens,
		uint256[] calldata prices,
		bool canBePurchasedInFiat,
		uint256 priceInFiat
	) external {
		require(depositToken != address(0), "Invalid deposit token address");
		require(amount > 0, "Amount must be greater than zero");
		require(
			acceptedTokens.length == prices.length,
			"Accepted tokens and prices length mismatch"
		);

		TokenInfo storage info = depositTokenInfo[depositToken];
		info.depositTokenOwner = msg.sender;
		info.priceInEth = priceInEth;
		info.acceptedTokens = acceptedTokens;
		info.depositTokenBalance = amount;
		info.canBePurchasedInFiat = canBePurchasedInFiat;
		info.priceInFiat = priceInFiat;
		info.isNFT = false;

		for (uint256 i = 0; i < acceptedTokens.length; i++) {
			info.acceptedTokenPrices[acceptedTokens[i]] = prices[i];
		}

		IERC20(depositToken).transferFrom(msg.sender, address(this), amount);

		isDepositToken[depositToken] = true;
		trackToken(depositToken);

		emit TokenDeposited(depositToken, msg.sender, amount, priceInEth);
	}

	// List NFT for sale
	function listNFTForSale(
		address nftContract,
		uint256 tokenId,
		uint256 priceInEth,
		address[] calldata acceptedTokens,
		uint256[] calldata prices,
		bool canBePurchasedInFiat,
		uint256 priceInFiat
	) external {
		require(nftContract != address(0), "Invalid NFT contract address");
		require(
			acceptedTokens.length == prices.length,
			"Accepted tokens and prices length mismatch"
		);

		// Verify ownership and approval
		require(
			IERC721(nftContract).ownerOf(tokenId) == msg.sender,
			"Not the NFT owner"
		);
		require(
			IERC721(nftContract).getApproved(tokenId) == address(this) ||
				IERC721(nftContract).isApprovedForAll(msg.sender, address(this)),
			"Contract not approved for NFT transfer"
		);

		TokenInfo storage info = depositTokenInfo[nftContract];
		info.depositTokenOwner = msg.sender;
		info.priceInEth = priceInEth;
		info.acceptedTokens = acceptedTokens;
		info.canBePurchasedInFiat = canBePurchasedInFiat;
		info.priceInFiat = priceInFiat;
		info.isNFT = true;
		info.tokenId = tokenId;

		for (uint256 i = 0; i < acceptedTokens.length; i++) {
			info.acceptedTokenPrices[acceptedTokens[i]] = prices[i];
		}

		IERC721(nftContract).transferFrom(msg.sender, address(this), tokenId);

		isDepositToken[nftContract] = true;
		trackToken(nftContract);

		emit TokenDeposited(nftContract, msg.sender, 1, priceInEth);
	}

	// Update price function
	function updatePrice(
		address depositToken,
		address purchaseToken,
		uint256 newPrice,
		bool canBePurchasedInFiat,
		uint256 newPriceInFiat
	) external {
		TokenInfo storage info = depositTokenInfo[depositToken];
		require(
			msg.sender == info.depositTokenOwner,
			"Only the deposit token owner can update prices"
		);

		require(newPrice >= 0, "New price must be non-negative");

		if (purchaseToken == address(0)) {
			// Update ETH price
			info.priceInEth = newPrice;
		} else {
			// Update ERC-20 token price
			info.acceptedTokenPrices[purchaseToken] = newPrice;

			bool tokenExists = false;
			for (uint256 i = 0; i < info.acceptedTokens.length; i++) {
				if (info.acceptedTokens[i] == purchaseToken) {
					tokenExists = true;
					break;
				}
			}
			if (!tokenExists && newPrice > 0) {
				info.acceptedTokens.push(purchaseToken);
			} else if (newPrice == 0) {
				// Remove the token from acceptedTokens if price is set to 0
				for (uint256 i = 0; i < info.acceptedTokens.length; i++) {
					if (info.acceptedTokens[i] == purchaseToken) {
						info.acceptedTokens[i] = info.acceptedTokens[
							info.acceptedTokens.length - 1
						];
						info.acceptedTokens.pop();
						break;
					}
				}
			}
		}

		info.canBePurchasedInFiat = canBePurchasedInFiat;
		info.priceInFiat = newPriceInFiat;

		emit PriceUpdated(depositToken, purchaseToken, newPrice);
	}

	// Buy token function
	function buyToken(
		address depositToken,
		address purchaseToken,
		uint256 amount
	) public payable {
		TokenInfo storage info = depositTokenInfo[depositToken];
		require(isDepositToken[depositToken], "Token not for sale");
		require(info.isNFT ? amount == 1 : amount > 0, "Invalid amount for token type");

		uint256 price;
		if (purchaseToken == address(0)) {
			price = info.priceInEth;
			require(price > 0, "ETH not accepted for this token");
		} else {
			price = info.acceptedTokenPrices[purchaseToken];
			require(price > 0, "Payment token not accepted");
		}

		uint256 totalPrice = price * amount;
		uint256 fee = (totalPrice * feePercent) / 100;
		uint256 netPrice = totalPrice - fee;

		if (purchaseToken == address(0)) {
			// ETH payment
			require(msg.value == totalPrice, "Incorrect ETH amount sent");
			ethFees += fee;
			info.ethBalance += netPrice;
			info.totalSalesInEth += netPrice;
		} else {
			// ERC20 payment
			IERC20(purchaseToken).transferFrom(
				msg.sender,
				address(this),
				totalPrice
			);
			tokenFees[purchaseToken] += fee;
			info.tokenBalance[purchaseToken] += netPrice;
			info.totalSalesInTokens[purchaseToken] += netPrice;
			trackToken(purchaseToken);
		}

		if (info.isNFT) {
			IERC721(depositToken).transferFrom(address(this), msg.sender, info.tokenId);
			isDepositToken[depositToken] = false; // Remove NFT from sale after purchase
		} else {
			uint256 contractBalance = IERC20(depositToken).balanceOf(address(this));
			require(contractBalance >= amount, "Not enough tokens in contract");
			uint256 sendAmount = amount * 1 ether;
			IERC20(depositToken).transfer(msg.sender, sendAmount);
			info.depositTokenBalance -= sendAmount;
		}

		emit TokenPurchased(
			msg.sender,
			depositToken,
			purchaseToken,
			netPrice,
			amount
		);
	}

	function fiatPurchase(
		address depositToken,
		address buyer,
		uint256 amountPurchased,
		uint256 totalPrice
	) external {
		require(
			msg.sender == paymentOracle,
			"Only the oracle can call this function"
		);
		TokenInfo storage info = depositTokenInfo[depositToken];
		require(isDepositToken[depositToken], "Token not for sale");
		info.totalSalesInFiat += totalPrice;

		if (info.isNFT) {
			IERC721(depositToken).transferFrom(address(this), buyer, info.tokenId);
			isDepositToken[depositToken] = false; // Remove NFT from sale after purchase
		} else {
			uint256 contractBalance = IERC20(depositToken).balanceOf(address(this));
			require(
				contractBalance >= amountPurchased,
				"Not enough tokens in contract"
			);

			IERC20(depositToken).transfer(buyer, amountPurchased);
			info.depositTokenBalance -= amountPurchased;
		}

		emit FiatPurchase(buyer, depositToken, amountPurchased);
	}

	function trackToken(address tokenToTrack) internal {
		bool tokenTracked = false;
		for (uint256 i = 0; i < trackedTokens.length; i++) {
			if (trackedTokens[i] == tokenToTrack) {
				tokenTracked = true;
				break;
			}
		}
		if (!tokenTracked) {
			trackedTokens.push(tokenToTrack);
		}
	}

	// Set fee percentage
	function setFeePercent(uint256 newFeePercent) external onlyOwner {
		require(
			newFeePercent >= 0 && newFeePercent <= 100,
			"Invalid fee percent"
		);
		feePercent = newFeePercent;

		emit FeeUpdated(newFeePercent);
	}

	// Update payment oracle
	function setPaymentOracle(address newOracle) external onlyOwner {
		require(newOracle != address(0), "Invalid oracle address");
		paymentOracle = newOracle;
	}

	// Withdraw tokens and ETH sales proceeds
	function withdrawSalesProceeds(address depositToken) public {
		TokenInfo storage info = depositTokenInfo[depositToken];
		require(msg.sender == info.depositTokenOwner, "Not authorized");

		uint256 ethAmount = info.ethBalance;
		if (ethAmount > 0) {
			payable(msg.sender).transfer(ethAmount);
			info.ethBalance = 0;
			emit TokensWithdrawn(address(0), msg.sender, ethAmount);
		}

		for (uint256 i = 0; i < info.acceptedTokens.length; i++) {
			address token = info.acceptedTokens[i];
			uint256 tokenAmount = info.tokenBalance[token];
			if (tokenAmount > 0) {
				IERC20(token).transfer(msg.sender, tokenAmount);
				info.tokenBalance[token] = 0;
				emit TokensWithdrawn(token, msg.sender, tokenAmount);
			}
		}
	}

	// Remove deposit function
	function removeDeposit(address depositToken) external {
		TokenInfo storage info = depositTokenInfo[depositToken];
		require(
			msg.sender == info.depositTokenOwner || msg.sender == owner(),
			"Not authorized"
		);

		// Withdraw sales proceeds first
		withdrawSalesProceeds(depositToken);

		if (info.isNFT) {
			IERC721(depositToken).transferFrom(address(this), msg.sender, info.tokenId);
		} else {
			uint256 contractBalance = IERC20(depositToken).balanceOf(address(this));
			if (contractBalance > 0) {
				IERC20(depositToken).transfer(msg.sender, contractBalance);
			}
		}

		// Remove the token from sale
		isDepositToken[depositToken] = false;

		emit DepositRemoved(depositToken, msg.sender, info.depositTokenBalance);
	}

	// Collect fees
	function collectFees() external onlyOwner {
		uint256 ethAmount = ethFees;
		ethFees = 0;
		payable(owner()).transfer(ethAmount);

		address[] memory tokens = new address[](trackedTokens.length);
		uint256[] memory amounts = new uint256[](trackedTokens.length);

		for (uint256 i = 0; i < trackedTokens.length; i++) {
			address token = trackedTokens[i];
			uint256 amount = tokenFees[token];
			if (amount > 0) {
				tokenFees[token] = 0;
				tokens[i] = token;
				amounts[i] = amount;
				IERC20(token).transfer(owner(), amount);
			}
		}

		emit FeesCollected(ethAmount, tokens, amounts);
	}
}

